// buat list nama aset tanpa '_idr' (genap)
$arrayA = [];
foreach ($daftaraset as $key => $value) {
    if ($key % 2 == 0) {
        $value = str_replace('_idr', '', $value);
        $arrayA[] = $value;
    }
}

// buat list nama aset tanpa '_idr' (ganjil)
$arrayB = [];
foreach ($daftaraset as $key => $value) {
    if ($key % 2 != 0) {
        $value = str_replace('_idr', '', $value);
        $arrayB[] = $value;
    }
}

// buat list nama aset tanpa '_idr' urutan bilangan prima
$arrayC = [];
foreach ($daftaraset as $key => $value) {
    $a = 0;
    for ($i = 1; $i <= $key; $i++) {
        if ($key % $i == 0) {
            $a++;
        }
    }
    if ($a == 2) {
        $value = str_replace('_idr', '', $value);
        $arrayC[] = $value;
    }
}

// buat list nama aset tanpa '_idr' yang memiliki awalan a,c,e,g,i,k,m,o,q,s,u,w, dan y
$arrayD = [];
foreach ($daftaraset as $key => $value) {
    $value = str_replace('_idr', '', $value);
    $value = strtolower($value);
    $awal = substr($value, 0, 1);
    if ($awal == 'a' || $awal == 'c' || $awal == 'e' || $awal == 'g' || $awal == 'i' || $awal == 'k' || $awal == 'm' || $awal == 'o' || $awal == 'q' || $awal == 's' || $awal == 'u' || $awal == 'w' || $awal == 'y') {
        $arrayD[] = $value;
    }
}

